INSERT INTO RT153970.CARTE_LANGUE (CARTE_NOM, CARTE_TEXTE, CARTE_ID, LANG_ID) VALUES ('Fragmentation', 'Détruisez l''artefact ou l''enchantement avec un coût converti de mana inférieur ou égal à 4 ciblé.', 97, 2);
INSERT INTO RT153970.CARTE_LANGUE (CARTE_NOM, CARTE_TEXTE, CARTE_ID, LANG_ID) VALUES ('Acrobatic Maneuver', 'Exile target creature you control, then return that card to the battlefield under its owner''s control. Draw a card.', 84, 1);
INSERT INTO RT153970.CARTE_LANGUE (CARTE_NOM, CARTE_TEXTE, CARTE_ID, LANG_ID) VALUES ('Aerial Responder', 'Flying, vigilance, lifelink', 85, 1);
INSERT INTO RT153970.CARTE_LANGUE (CARTE_NOM, CARTE_TEXTE, CARTE_ID, LANG_ID) VALUES ('Aetherstorm Roc', 'Whenever Aetherstorm Roc or another creature enters the battlefield under your control, you get {E} (an energy counter). Whenever Aetherstorm Roc attacks, you may pay {E}{E}. If you do, put a +1/+1 counter on it and tap up to one target creature defending player controls.', 86, 1);